import './App.css';

import React, { useEffect, useState } from 'react';
import { FormGroup, Input, Label } from 'reactstrap';
import Select from 'react-select';

import axios from 'axios';

function App() {
  const [isDisabled, setIsDisabled] = useState(true);
  const [city, setCity] = useState([]);
  const [district, setDistrict] = useState([]);
  const [refresh, setRefresh] = useState(false);
  const [type, setType] = useState([]);
  const [districtFilt, setDistrictFilt] = useState([]);
  const [addressType, setAddressType] = useState([]);
  const [personalData, setPersonalData] = useState([]);
  const [employee, setEmployee] = useState({
    employee_name: "",
    employee_surname: "",
    employee_birthday: "",
    employee_birthplace: "",
    city_id: "",
    district_id: "",
    address_type_id: "",

  });

  useEffect(() => {
    axios.get('http://127.0.0.1:8000/api/getemployee').then((response) => {
      setPersonalData(response.data.employee);
      setCity(response.data.city);
      setDistrict(response.data.district);
      setAddressType(response.data.type);
    })
  }, [refresh])

  const onChangeCity = (e) => {
    console.log(e);
    setDistrictFilt(district.filter((district) => e.value === district.city_id));
    setIsDisabled(false);
    setEmployee((value) => ({ ...value, city_id: e.value }));
    setRefresh(!refresh);

  }





  return (
    <div className='container'>

      <div className='row'>
        <div className='col-lg-6'>
          <h2>Add Employee</h2>
          <FormGroup>
            <Label for="name"><h5>Please enter name</h5></Label>
            <Input type="email" name="email" placeholder="Please enter name" value={employee.employee_name} onChange={(e) => setEmployee((value) => ({ ...value, employee_name: e.target.value }))} />
          </FormGroup>
          <FormGroup>
            <Label for="surname"><h5>Please enter surname</h5></Label>
            <Input type="text" name="text" placeholder="with a placeholder" value={employee.employee_surname} onChange={(e) => setEmployee((value) => ({ ...value, employee_surname: e.target.value }))} />
          </FormGroup>
          <FormGroup>
            <Label for="birthday">Please enter birth day</Label>
            <Input type="date" name="birthday" placeholder="with a placeholder" value={employee.employee_birthday} onChange={(e) => setEmployee((value) => ({ ...value, employee_birthday: e.target.value }))} />
          </FormGroup>
          <FormGroup>
            <Label for="birthplace">Please enter birthplace</Label>
            <Input type="text" name="birthplace" placeholder="with a placeholder" value={employee.employee_birthplace} onChange={(e) => setEmployee((value) => ({ ...value, employee_birthplace: e.target.value }))} />
          </FormGroup>

        </div>
        <div className='col-lg-6'>
          <h2>Add Address</h2>
          <label><h5>Please select city</h5></label>
          <Select options={city} onChange={(e) => onChangeCity(e)} />
          <label><h5>Please select district</h5></label>
          <Select options={districtFilt} isDisabled={isDisabled} />
          <label><h5>Please select type</h5></label>
          <Select options={type} />
        </div>
        <button className='btn btn-success'>Save</button>
      </div>

    </div>


  );
}

export default App;
